mapper.select:
	SELECT * FROM (
	    SELECT ROWNUM rnum, tb.* FROM (
		     SELECT guestNo,g.memberNo, name, g.content, g.created,groupURL
		        FROM guestBook_${url} g 
                JOIN member_${url} m ON g.memberNo=m.memberNo
                ORDER BY guestNo DESC

        ) tb WHERE ROWNUM <= #{end}
    ) WHERE rnum >= #{start}
