mapper.update:
UPDATE community 
SET name=#{name}, area=#{area}, verification=#{verification}, bankName=#{bankName},
	accountNumber=#{accountNumber}, accountHolder=#{accountHolder}, 
	userId=#{userId}
WHERE groupURL=#{groupURL}

mapper.select:
SELECT groupURL, groupName, area, bankName, accountNumber, accountHolder, userId, foundationDay
FROM community 

WHERE groupURL=#{groupURL}
